windowclass.script:
function onInit()
	if super and super.onInit then
		super.onInit();
	end
	if not windowlist.isReadOnly() then
		registerMenuItem(Interface.getString("power_menu_addresource"), "coins", 3, 6);
	end
end
function onMenuSelection(selection, subselection)
	if super and super.onMenuSelection then
		super.onMenuSelection(selection, subselection);
	end
	if selection == 3 and subselection == 6 then
		createAction("resource");
		activatedetail.setValue(1);
	end
end

string_poweractionview.script:
function onWheel(notches)
	if Input.isControlPressed() then
		local node = window.getDatabaseNode();
		if DB.getValue(node, "variable", 0) == 1 then
			local nModifier = DB.getValue(node, "modifier", 0);
			local nMin = DB.getValue(node, "min", 0);
			local nMax = DB.getValue(node, "max", 0);
			local nInternval = DB.getValue(node, "interval", 0);
			nModifier = nModifier + (notches * nInternval);
			nModifier = math.max(nMin, nModifier);
			if nMax > nMin then
				nModifier = math.min(nMax, nModifier);
			end
			DB.setValue(node, "modifier", "number", nModifier);
		end
	end
	return true;
end
